mod test;

contract Vanhelsing {
    use dep::token::Token;
    use dep::aztec::{prelude::{PublicMutable, AztecAddress, Map}};

    #[aztec(storage)]
    struct Storage {
        feeTo: PublicMutable<AztecAddress>,
    }

    #[aztec(public)]
    #[aztec(initializer)]
    fn constructor(
        feeTo: AztecAddress,
    ) {
        storage.feeTo.write(feeTo);
    }

    #[aztec(public)]
    fn createCoffin(
        stakingToken: AztecAddress,
        rewardsToken: AztecAddress,
        periodInDays: Field,
        rewardsAmount: Field,
        nonce: Field
    ) {

        Token::at(rewardsToken).transfer_public(
            context.msg_sender(),
            context.this_address(),
            rewardsAmount,
            nonce
        ).call(&mut context);

    }

}

